mapper.select:
select

from public.act_goods
WHERE deleted = '0'

    AND activity_id = #{activityId,jdbcType=VARCHAR}



    AND goods_id = #{goodsId,jdbcType=VARCHAR}



    AND sku_id = #{skuId,jdbcType=VARCHAR}

order by act_price

select master.goods_id,erp.sku from view_erp_goods erp left join gds_master master on erp.goods_code = master.goods_code where erp.sell_year = #{activityGoodsSellYear}
AND season_code = #{activityGoodsSeasonCode,jdbcType=VARCHAR}
and master.is_onsell = '0' and master.deleted = '0'
