mapper.select:
select * from act_master am where am.deleted = '0'
and am.shop_id = #{shopId,jdbcType=VARCHAR}
order by am.update_time desc

select master.* from act_master master left join act_template templ on master.temp_id = templ.temp_id where master.deleted = '0'
and master.shop_id = #{shopId,jdbcType=VARCHAR}
and master.approve_status = '20' and (templ.actition_type='10' or templ.actition_type = '20') and master.start_time <= now() and master.end_time >= now() ORDER by master.sort,master.update_time desc

select master.* from act_master master left join act_template templ on master.temp_id = templ.temp_id where master.deleted = '0'
and master.shop_id = #{shopId,jdbcType=VARCHAR}
and master.approve_status = '20' and master.start_time <= now() and master.end_time >= now() ORDER by master.sort,master.update_time desc

select master.*
from act_master master
inner join act_template templ on master.temp_id = templ.temp_id and templ.actition_type='11'
where master.deleted = '0'

    and master.shop_id = #{shopId,jdbcType=VARCHAR}

and master.approve_status = '20'
and master.start_time <= #{startTime,jdbcType=TIMESTAMP}
and master.end_time >= #{endTime,jdbcType=TIMESTAMP}
order by to_char(master.start_time, 'HH24:MI')

select master.*
from act_master master
inner join act_template templ on master.temp_id = templ.temp_id and templ.actition_type='11'
where master.deleted = '0'

    and master.shop_id = #{shopId,jdbcType=VARCHAR}

and master.approve_status = '20'
and master.start_time <= #{startTime,jdbcType=TIMESTAMP}
and master.end_time >= #{startTime,jdbcType=TIMESTAMP}
and EXTRACT(HOUR from master.start_time) = #{hour}
and EXTRACT(MINUTE from master.start_time) = #{minute}
order by to_char(master.start_time, 'HH24:MI')

select master.* from act_master master left join act_template templ on master.temp_id = templ.temp_id where master.deleted = '0'
and master.shop_id = #{shopId,jdbcType=VARCHAR}
and master.approve_status = '20' and templ.actition_type='30' and master.start_time <= now() and master.end_time >= now() ORDER by master.sort,master.update_time desc

select am.*, user1.user_name as apply_user_name, user2.user_name as approve_user_name, code1.value as actition_type, code1.display_cn as actition_type_cn, code1.display_en as actition_type_en, code2.display_cn as approve_status_cn, code2.display_en as approve_status_en from act_master am LEFT JOIN act_template at ON am.temp_id = at.temp_id LEFT JOIN sys_user user1 ON am.apply_user_id = user1.login_id LEFT JOIN sys_user user2 ON am.approve_user_id = user2.login_id LEFT JOIN com_code code1 ON code1.code = 'ACTITION_TYPE' and at.actition_type = code1.value LEFT JOIN com_code code2 ON code2.code = 'APPROVE_STATUS' and am.approve_status = code2.value
am.temp_name LIKE CONCAT('%', #{tempName},'%')
AND am.activity_name LIKE CONCAT('%', #{activityName},'%')
AND am.unit LIKE CONCAT('%', #{unit},'%')
AND at.actition_type = #{actitionType}
AND am.approve_status = #{approveStatus}
AND am.deleted = #{deleted}
order by sort,insert_time desc limit #{iDisplayLength,jdbcType=INTEGER} offset #{iDisplayStart,jdbcType=INTEGER}

select count(1)
from act_master am



            am.temp_name LIKE CONCAT('%', #{tempName},'%')


            AND am.activity_name LIKE CONCAT('%', #{activityName},'%')


            AND am.unit LIKE CONCAT('%', #{unit},'%')


            AND am.actition_type = #{actitionType}


            AND am.approve_status = #{approveStatus}


            AND am.deleted = #{deleted}

select am.*, user1.user_name as apply_user_name, user2.user_name as approve_user_name, code1.value as actition_type, code1.display_cn as actition_type_cn, code1.display_en as actition_type_en, code2.display_cn as approve_status_cn, code2.display_en as approve_status_en from act_master am LEFT JOIN act_template at ON am.temp_id = at.temp_id LEFT JOIN sys_user user1 ON am.apply_user_id = user1.login_id LEFT JOIN sys_user user2 ON am.approve_user_id = user2.login_id LEFT JOIN com_code code1 ON code1.code = 'ACTITION_TYPE' and at.actition_type = code1.value LEFT JOIN com_code code2 ON code2.code = 'APPROVE_STATUS' and am.approve_status = code2.value where am.approve_status != '40'
AND am.temp_name LIKE CONCAT('%', #{tempName},'%')
AND am.activity_name LIKE CONCAT('%', #{activityName},'%')
AND at.actition_type = #{actitionType}
AND am.approve_status = #{approveStatus}
AND am.deleted = #{deleted}
order by am.approve_status,apply_time desc,approve_time desc limit #{iDisplayLength,jdbcType=INTEGER} offset #{iDisplayStart,jdbcType=INTEGER}

select count(1)
from act_master am
LEFT JOIN act_template at ON am.temp_id = at.temp_id
where am.approve_status != '40'

    AND am.temp_name LIKE CONCAT('%', #{tempName},'%')


    AND am.activity_name LIKE CONCAT('%', #{activityName},'%')


    AND at.actition_type = #{actitionType}


    AND am.approve_status = #{approveStatus}


    AND am.deleted = #{deleted}

select DISTINCT amu.* from ( -- "10";"全部" select am.activity_id,am.temp_id,at.actition_type as activity_type, am.activity_name, am.goods_type, am.member_type, am.start_time, am.end_time from act_master am left join act_template at on am.temp_id = at.temp_id where am.goods_type='10' and am.unit='10' and am.approve_status = '20' and am.start_time <= now() and am.end_time >= now() and am.deleted = '0' UNION
-- "20";"按分类" select am.activity_id,am.temp_id,at.actition_type as activity_type, am.activity_name, am.goods_type, am.member_type, am.start_time, am.end_time from act_master am left join act_goods ag on am.activity_id = ag.activity_id left join act_template at on am.temp_id = at.temp_id where ag.goods_type='20' and am.unit='10' and am.approve_status = '20' and am.start_time <= now() and am.end_time >= now() and am.deleted = '0' and ag.goods_id = #{goodsTypeId}
UNION
-- "30";"按品牌" select am.activity_id, am.temp_id,at.actition_type as activity_type,am.activity_name, am.goods_type, am.member_type, am.start_time, am.end_time from act_master am left join act_goods ag on am.activity_id = ag.activity_id left join act_template at on am.temp_id = at.temp_id where ag.goods_type='30' and am.unit='10' and am.approve_status = '20' and am.start_time <= now() and am.end_time >= now() and am.deleted = '0' and ag.goods_id = #{brandId}
UNION
-- "40";"按商品" select am.activity_id, am.temp_id,at.actition_type as activity_type,am.activity_name, am.goods_type, am.member_type, am.start_time, am.end_time from act_master am left join act_goods ag on am.activity_id = ag.activity_id left join act_template at on am.temp_id = at.temp_id where ag.goods_type='40' and am.unit='10' and am.approve_status = '20' and am.start_time <= now() and am.end_time >= now() and am.deleted = '0' and ag.goods_id = #{goodsId}
UNION
-- "50";"按SKU" select am.activity_id, am.temp_id,at.actition_type as activity_type,am.activity_name, am.goods_type, am.member_type, am.start_time, am.end_time from act_master am left join act_goods ag on am.activity_id = ag.activity_id left join act_template at on am.temp_id = at.temp_id where ag.goods_type='50' and am.unit='10' and am.approve_status = '20' and am.start_time <= now() and am.end_time >= now() and am.deleted = '0' and ag.goods_id = #{goodsId} and ag.sku_id = #{skuid} ) amu

select activity_id,activity_name from act_master
where to_char(end_time, 'yyyy-mm-dd') >= #{orderTimeStart,jdbcType=VARCHAR}
and to_char(end_time, 'yyyy-mm-dd') <= #{orderTimeEnd,jdbcType=VARCHAR}

select coupon_id,coupon_name from coupon_master
where to_char(end_time, 'yyyy-mm-dd') >= #{orderTimeStart,jdbcType=VARCHAR}
and to_char(end_time, 'yyyy-mm-dd') <= #{orderTimeEnd,jdbcType=VARCHAR}

