-- "30";"按品牌" select am.activity_id, am.temp_id,at.actition_type as activity_type,am.activity_name, am.goods_type, am.member_type, am.start_time, am.end_time from act_master am left join act_goods ag on am.activity_id = ag.activity_id left join act_template at on am.temp_id = at.temp_id where ag.goods_type='30' and am.unit='10' and am.approve_status = '20' and am.start_time <= now() and am.end_time >= now() and am.deleted = '0' and ag.goods_id = #{brandId}
UNION
-- "40";"按商品" select am.activity_id, am.temp_id,at.actition_type as activity_type,am.activity_name, am.goods_type, am.member_type, am.start_time, am.end_time from act_master am left join act_goods ag on am.activity_id = ag.activity_id left join act_template at on am.temp_id = at.temp_id where ag.goods_type='40' and am.unit='10' and am.approve_status = '20' and am.start_time <= now() and am.end_time >= now() and am.deleted = '0' and ag.goods_id =#{goodsId}
UNION
-- "50";"按SKU" select am.activity_id, am.temp_id,at.actition_type as activity_type,am.activity_name, am.goods_type, am.member_type, am.start_time, am.end_time from act_master am left join act_goods ag on am.activity_id = ag.activity_id left join act_template at on am.temp_id = at.temp_id where ag.goods_type='50' and am.unit='10' and am.approve_status = '20' and am.start_time <= now() and am.end_time >= now() and am.deleted = '0' and ag.goods_id =#{goodsId}
and ag.sku_id in #{skuIdList}
) amu
