mapper.select:
select , cct.display_cn as item_type_name, su.user_name as insert_user_name, ci.item_name as item_name from public.cms_master cm left join com_code cct on cct.code = 'CMS_MASTER_TYPE' and cct.value = cm.item_type left join sys_user su on cast(su.user_id as varchar) = cm.insert_user_id left join cms_items ci on ci.item_id = cm.item_id where cm.deleted = '0'
and cm.title LIKE '%' || #{title,jdbcType=VARCHAR} || '%'
and cm.item_type = #{itemType,jdbcType=VARCHAR}
and cm.item_id = #{itemId,jdbcType=VARCHAR}
order by cm.update_time DESC limit #{needColumns,jdbcType=INTEGER} offset #{startPoint,jdbcType=INTEGER}

select count(*) as count from public.cms_master cm where cm.deleted = '0'
and cm.title LIKE '%' || #{title,jdbcType=VARCHAR} || '%'
and cm.item_type = #{itemType,jdbcType=VARCHAR}
and cm.item_id = #{itemId,jdbcType=VARCHAR}

mapper.update:
update public.cms_master
set deleted = '1'
where cms_id = #{cmsId,jdbcType=VARCHAR}
