mapper.select:
select
mlr.member_level_id, mlr.member_level_code, mlr.point_lower,mlr.point_upper, mlr.discount, mlr.point_ratio, mlr.comment, mlr.deleted, mlr.update_user_id, mlr.update_time, mlr.insert_user_id, mlr.insert_time, mlr.point_single, mlr.point_cumulative, su1.user_name as insert_user_name, su2.user_name as update_user_name,mmc.member_count, cc.display_cn as member_level_name from public.mmb_level_rule mlr left join sys_user su1 on cast(su1.user_id as varchar) = mlr.insert_user_id left join sys_user su2 on cast(su2.user_id as varchar) = mlr.update_user_id left join com_code cc on cc.code = 'MEMBER_LEVEL' and cc.deleted = '0' and cc.value = mlr.member_level_code left join ( select count(member_id) member_count, member_level_id from mmb_master where deleted = '0' group by member_level_id ) mmc on mmc.member_level_id = mlr.member_level_code where mlr.deleted = '0'
AND mlr.member_level_name LIKE CONCAT(CONCAT('%', #{memberLevelName}),'%')
AND mlr.member_level_code = #{memberLevelCode}
order by mlr.member_level_code, mlr.update_time DESC limit #{iDisplayLength,jdbcType=INTEGER} offset #{iDisplayStart,jdbcType=INTEGER}

select mlr.*, su1.user_name as insert_user_name, su2.user_name as update_user_name from public.mmb_level_rule mlr left join sys_user su1 on cast(su1.user_id as varchar) = mlr.insert_user_id left join sys_user su2 on cast(su2.user_id as varchar) = mlr.update_user_id where mlr.deleted = '0'
AND mlr.member_level_code = #{memberLevelCode}
AND mlr.member_level_id = #{memberLevelId}

select member.*, cc.display_cn current_level_name, cc1.display_cn approval_level_name from view_approval_up_member member inner join (select member_id, max(approval_level_id) max_approval_level_id from view_approval_up_member group by member_id) ma on ma.member_id = member.member_id and member.approval_level_id = ma.max_approval_level_id left join com_code cc on cc.code = 'MEMBER_LEVEL' and cc.value = member.current_level_id left join com_code cc1 on cc1.code = 'MEMBER_LEVEL' and cc1.value = member.approval_level_id
where member.approval_level_id > member.current_level_id
AND member.current_level_id = #{currentLevelId}
order by member.approval_level_id DESC, member.current_level_id DESC, member.member_name limit #{iDisplayLength,jdbcType=INTEGER} offset #{iDisplayStart,jdbcType=INTEGER}

select member.*, cc.display_cn current_level_name, cc1.display_cn approval_level_name from view_approval_up_memberintwo member inner join (select member_id, max(approval_level_id) max_approval_level_id from view_approval_up_memberintwo group by member_id) ma on ma.member_id = member.member_id and member.approval_level_id = ma.max_approval_level_id left join com_code cc on cc.code = 'MEMBER_LEVEL' and cc.value = member.current_level_id left join com_code cc1 on cc1.code = 'MEMBER_LEVEL' and cc1.value = member.approval_level_id
where member.approval_level_id > member.current_level_id
AND member.current_level_id = #{currentLevelId}
/*添加手机号搜索功能*/
and member.telephone like CONCAT('%', #{telephone,jdbcType=VARCHAR}, '%')
/*添加了年份标识判断*/ /*当yearNum=0时，查询当年消费*/
and y_time = to_char((SELECT now()::timestamp),'yyyy')
/*当yearNum=1时，查询当年和前一年的消费*/
and y_time >= to_char((SELECT now()::timestamp+ '-1 year'),'yyyy') and y_time <= to_char((SELECT now()::timestamp),'yyyy')
order by member.approval_level_id DESC, member.current_level_id DESC, member.member_name limit #{iDisplayLength,jdbcType=INTEGER} offset #{iDisplayStart,jdbcType=INTEGER}

select member.*, cc.display_cn current_level_name, cc1.display_cn approval_level_name from view_approval_up_memberintwo member inner join (select member_id, max(approval_level_id) max_approval_level_id from view_approval_up_memberintwo group by member_id) ma on ma.member_id = member.member_id and member.approval_level_id = ma.max_approval_level_id left join com_code cc on cc.code = 'MEMBER_LEVEL' and cc.value = member.current_level_id left join com_code cc1 on cc1.code = 'MEMBER_LEVEL' and cc1.value = member.approval_level_id
where member.approval_level_id > member.current_level_id
AND member.current_level_id = #{currentLevelId}
/*添加手机号搜索功能*/
and member.telephone like CONCAT('%', #{telephone,jdbcType=VARCHAR}, '%')
/*添加了年份标识判断*/ /*当yearNum=0时，查询当年消费*/
and y_time = to_char((SELECT now()::timestamp),'yyyy')
/*当yearNum=1时，查询当年和前一年的消费*/
and y_time >= to_char((SELECT now()::timestamp+ '-1 year'),'yyyy') and y_time <= to_char((SELECT now()::timestamp),'yyyy')

SELECT count(*) FROM view_approval_up_memberInTwo
/*添加了年份标识判断*/
/*当yearNum=0时，查询当年消费*/

    where y_time = to_char((SELECT now()::timestamp),'yyyy')

/*当yearNum=1时，查询当年和前一年的消费*/

    where y_time >= to_char((SELECT now()::timestamp+ '-1 year'),'yyyy')
    and y_time <= to_char((SELECT now()::timestamp),'yyyy')
