mapper.select:
select mpr.*
    from PUBLIC.mmb_point_record mpr
    where mpr.deleted = '0'
    and mpr.member_id = #{memberId}
    order by mpr.point_time desc, mpr.record_no desc
    limit 10 offset #{currentPage,jdbcType=INTEGER}

mapper.update:
update mmb_point_record
set update_user_id = 'ERP',
update_time = now(),
point_surplus = #{pointSurplus,jdbcType=INTEGER}
where record_no = #{recordNo,jdbcType=INTEGER}

mapper.select:
select record_no, member_id, (case when r.insert_user_id='ERP' then 'ERP' else '电商' end) as type_name, rule_id, point, point_time, score_source, point_surplus, point_ratio, point_cash, erp_send_status,c.display_cn rule_name from mmb_point_record r left join com_code c on c.value = r.rule_id and c.code='POINT_RULE' where member_id = #{memberId,jdbcType=VARCHAR} ORDER BY point_time
limit #{iDisplayLength,jdbcType=INTEGER}
offset #{iDisplayStart,jdbcType=INTEGER}

select count(*)
from mmb_point_record
where member_id = #{memberId,jdbcType=VARCHAR}
