  and member_name LIKE '%' || #{memberName,jdbcType=VARCHAR} || '%'


  and nick_name  LIKE '%' || #{nickName,jdbcType=VARCHAR} || '%'


  and telephone = #{telephone,jdbcType=VARCHAR}


