mapper.select:
select l.order_transfer_id, l.sub_order_id, l.order_id, l.transfer_status, c.display_cn transfer_status_name, l.sku, l.quantity, g.goods_name_cn goods_name, g.color_name colore_name, g.size_name, s1.store_name_cn apply_store, l.apply_user,l.apply_time, s2.store_name_cn dispatch_store, l.dispatch_time, u.user_name delivery_user, l.delivery_time, u1.user_name receive_user,l.receive_time from ord_transfer_list l left join view_erp_goods g on l.sku=g.sku left join com_code c on c.code='TRANSFER_STATUS' and c.value=l.transfer_status left join erp_store s1 on s1.store_code=l.apply_store left join erp_store s2 on s2.store_code=l.dispatch_store left join sys_user u on u.login_id=l.delivery_user left join sys_user u1 on u1.login_id=l.receive_user where l.deleted='0'
AND order_transfer_id = #{orderTransferId,jdbcType=VARCHAR}
AND order_id LIKE '%' || #{orderId,jdbcType=VARCHAR} || '%'
AND sub_order_id LIKE '%' || #{subOrderId,jdbcType=VARCHAR} || '%'
AND l.sku LIKE '%' || #{sku,jdbcType=VARCHAR} || '%'
AND transfer_status = #{transferStatus,jdbcType=VARCHAR}
AND apply_store = #{applyStore,jdbcType=VARCHAR}
AND dispatch_store = #{dispatchStore,jdbcType=VARCHAR}
ORDER by l.transfer_status,l.apply_time
limit #{iDisplayLength,jdbcType=INTEGER}
offset #{iDisplayStart,jdbcType=INTEGER}

select count(*)
from ord_transfer_list
where deleted='0'

  AND order_transfer_id = #{orderTransferId,jdbcType=VARCHAR}


  AND order_id LIKE '%' || #{orderId,jdbcType=VARCHAR} || '%'


  AND sub_order_id LIKE '%' || #{subOrderId,jdbcType=VARCHAR} || '%'


  AND sku LIKE '%' || #{sku,jdbcType=VARCHAR} || '%'


  AND transfer_status = #{transferStatus,jdbcType=VARCHAR}


  AND apply_store = #{applyStore,jdbcType=VARCHAR}


  AND dispatch_store = #{dispatchStore,jdbcType=VARCHAR}
